$(document).ready(function(){function t(t){var e=t.coords.latitude,t=t.coords.longitude;fetch(`/.netlify/functions/fetch-weather?lat=${e}&long=`+t).then(t=>t.json()).then(t=>{var e=t.weather,o=t.main,n=t.sys,t=t.name;e=(e=e[0]).icon,o=Math.trunc(o.temp),n=n.country,$(".icon").html(`<img src=http://openweathermap.org/img/wn/${e}@2x.png>`),$(".temperature").html(`<p>${o} &#8451</p>`),$(".location").html(`<p>${t}, ${n} </p>`)})}navigator.geolocation?(navigator.geolocation.getCurrentPosition(t),document.getElementById("weather").style.display="flex"):console.log("Geolocation must be authorized")});